import { useEffect, useState } from 'react';
import Styles from './TODO.module.css';
import { dummy } from './dummy';
import axios from 'axios';

export function TODO() {
    const [newTodo, setNewTodo] = useState('');
    const [newDescription, setNewDescription] = useState('');
    const [todoData, setTodoData] = useState(dummy);
    const [loading, setLoading] = useState(true);
    const [editing, setEditing] = useState(null);
    const [editTitle, setEditTitle] = useState('');
    const [editDescription, setEditDescription] = useState('');

    useEffect(() => {
        const fetchTodo = async () => {
            const apiData = await getTodo();
            setTodoData(apiData);
            setLoading(false);
        };
        fetchTodo();
    }, []);

    const getTodo = async () => {
        const options = {
            method: 'GET',
            url: `http://localhost:8000/api/todo`,
            headers: {
                accept: 'application/json',
            }
        };
        try {
            const response = await axios.request(options);
            return response.data;
        } catch (err) {
            console.log(err);
            return [];
        }
    };

    const addTodo = () => {
        const options = {
            method: 'POST',
            url: `http://localhost:8000/api/todo`,
            headers: {
                accept: 'application/json',
            },
            data: {
                title: newTodo,
                description: newDescription
            }
        };
        axios
            .request(options)
            .then((response) => {
                console.log(response.data);
                setTodoData((prevData) => [...prevData, response.data.newTodo]);
            })
            .catch((err) => {
                console.log(err);
            });
    };

    const deleteTodo = (id) => {
        const options = {
            method: 'DELETE',
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: 'application/json',
            }
        };
        axios
            .request(options)
            .then((response) => {
                console.log(response.data);
                setTodoData((prevData) => prevData.filter((todo) => todo._id !== id));
            })
            .catch((err) => {
                console.log(err);
            });
    };

    const updateTodo = (id, updatedFields) => {
        const options = {
            method: 'PATCH',
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: 'application/json',
            },
            data: updatedFields
        };
        axios
            .request(options)
            .then((response) => {
                console.log(response.data);
                setTodoData((prevData) =>
                    prevData.map((todo) =>
                        todo._id === id ? response.data : todo
                    )
                );
            })
            .catch((err) => {
                console.log(err);
            });
    };

    const handleEdit = (id, title, description) => {
        setEditing(id);
        setEditTitle(title);
        setEditDescription(description);
    };

    const saveEdit = (id) => {
        updateTodo(id, { title: editTitle, description: editDescription });
        setEditing(null);
    };

    return (
        <div className={Styles.ancestorContainer}>
            <div className={Styles.headerContainer}>
                <h1>Tasks</h1>
                <span>
                    <input
                        className={Styles.todoInput}
                        type='text'
                        name='New Todo'
                        placeholder='Task title'
                        value={newTodo}
                        onChange={(event) => setNewTodo(event.target.value)}
                    />
                    <input
                        className={Styles.todoInput}
                        type='text'
                        name='New Description'
                        placeholder='Task description'
                        value={newDescription}
                        onChange={(event) => setNewDescription(event.target.value)}
                    />
                    <button
                        id='addButton'
                        name='add'
                        className={Styles.addButton}
                        onClick={() => {
                            addTodo();
                            setNewTodo('');
                            setNewDescription('');
                        }}
                    >
                        + New Todo
                    </button>
                </span>
            </div>
            <div id='todoContainer' className={Styles.todoContainer}>
                {loading ? (
                    <p style={{ color: 'white' }}>Loading...</p>
                ) : (
                    todoData.length > 0 ? (
                        todoData.map((entry) => (
                            <div key={entry._id} className={Styles.todo}>
                                {editing === entry._id ? (
                                    <div>
                                        <input
                                            type='text'
                                            value={editTitle}
                                            onChange={(e) => setEditTitle(e.target.value)}
                                        />
                                        <input
                                            type='text'
                                            value={editDescription}
                                            onChange={(e) => setEditDescription(e.target.value)}
                                        />
                                        <button onClick={() => saveEdit(entry._id)}>Save</button>
                                        <button onClick={() => setEditing(null)}>Cancel</button>
                                    </div>
                                ) : (
                                    <div>
                                        <span className={Styles.infoContainer}>
                                            <input
                                                type='checkbox'
                                                checked={entry.done}
                                                onChange={() => {
                                                    updateTodo(entry._id, { done: !entry.done });
                                                }}
                                            />
                                            <strong>{entry.title}</strong>
                                            <p>{entry.description}</p>
                                        </span>
                                        <span
                                            style={{ cursor: 'pointer' }}
                                            onClick={() => handleEdit(entry._id, entry.title, entry.description)}
                                        >
                                            Edit
                                        </span>
                                        <span
                                            style={{ cursor: 'pointer' }}
                                            onClick={() => deleteTodo(entry._id)}
                                        >
                                            Delete
                                        </span>
                                    </div>
                                )}
                            </div>
                        ))
                    ) : (
                        <p className={Styles.noTodoMessage}>No tasks available. Please add a new task.</p>
                    )
                )}
            </div>
        </div>
    );
}
