import { useEffect, useState } from 'react'
import Styles from './TODO.module.css'
import { dummy } from './dummy'
import axios from 'axios';

export function TODO(props) {

    const [newTodo, setNewTodo] = useState('')
    const [todoData, setTodoData] = useState(dummy);
    const [loading, setLoading] = useState(true);
    const [editing, setEditing] = useState(null);
    const [editTitle, setEditTitle] = useState('');
    const [editDescription, setEditDescription] = useState('');


    useEffect(() => {
        const fetchTodo = async () => {
            const apiData = await getTodo()
            setTodoData(apiData);
            setLoading(false)
        }
        fetchTodo();
    }, [])

    const getTodo = async () => {
        const options = {
            method: "GET",
            url: `http://localhost:8000/api/todo`,
            headers: {
                accept: "application/json",
            }
        }
        try {
            const response = await axios.request(options)
            return response.data
        } catch (err) {
            console.log(err);
            return []; // return an empty array in case of error
        }
    }

    const addTodo = () => {
        const options = {
            method: "POST",
            url: `http://localhost:8000/api/todo`,
            headers: {
                accept: "application/json",
            },
            data: {
                title: newTodo
            }
        }
        axios
            .request(options)
            .then(function (response) {
                console.log(response.data)
                setTodoData(prevData => [...prevData, response.data.newTodo])
            })
            .catch((err) => {
                console.log(err)
            })
    }

    const deleteTodo = (id) => {
        const options = {
            method: "DELETE",
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: "application/json",
            }
        }
        axios
            .request(options)
            .then(function (response) {
                console.log(response.data)
                setTodoData(prevData => prevData.filter(todo => todo._id !== id))
            })
            .catch((err) => {
                console.log(err)
            })
    };

    const updateTodo = (id) => {
        const todoToUpdate = todoData.find(todo => todo._id === id)
        const options = {
            method: "PATCH",
            url: `http://localhost:8000/api/todo/${id}`,
            headers: {
                accept: "application/json",
            },
            data: {
                ...todoToUpdate,
                done: !todoToUpdate.done
            }
        }
        axios
            .request(options)
            .then(function (response) {
                console.log(response.data)
                setTodoData(prevData => prevData.map(todo => todo._id === id ? response.data : todo))
            })
            .catch((err) => {
                console.log(err)
            })
    };

    const handleEdit = (id, title, description) => {
        setEditing(id);
        setEditTitle(title);
        setEditDescription(description);
    };

    const saveEdit = (id) => {
        updateTodo(id, { title: editTitle, description: editDescription });
        setEditing(null);
    };

    const cancelEdit = () => {
        setEditing(null);
    };

    return (
        <div className={Styles.ancestorContainer}>
            <div className={Styles.headerContainer}>
                <h1>
                    Tasks
                </h1>
                <span>
                    <input
                        className={Styles.todoInput}
                        type='text'
                        name='New Todo'
                        value={newTodo}
                        onChange={(event) => {
                            setNewTodo(event.target.value)
                        }}
                    />
                    <button
                        id='addButton'
                        name='add'
                        className={Styles.addButton}
                        onClick={() => {
                            addTodo()
                            setNewTodo('')
                        }}
                    >
                        + New Todo
                    </button>
                </span>
            </div>
            <div id='todoContainer' className={Styles.todoContainer}>
            {loading ? (
                    <p style={{ color: 'white' }}>Loading...</p>
                ) : (
                    todoData.length > 0 ? (
                        todoData.map((entry) => (
                            <div key={entry._id} className={Styles.todo}>
                                {editing === entry._id ? (
                                    <div className={Styles.editContainer}>
                                        <div className={Styles.editMessage}>Editing...</div>
                                        <input
                                            type='text'
                                            value={editTitle}
                                            onChange={(e) => setEditTitle(e.target.value)}
                                            className={Styles.editInput}
                                        />
                                        <input
                                            type='text'
                                            value={editDescription}
                                            onChange={(e) => setEditDescription(e.target.value)}
                                            className={Styles.editInput}
                                        />
                                        <button
                                            onClick={() => saveEdit(entry._id)}
                                            className={Styles.saveButton}
                                        >
                                            Save
                                        </button>
                                        <button
                                            onClick={cancelEdit}
                                            className={Styles.cancelButton}
                                        >
                                            Cancel
                                        </button>
                                    </div>
                                ) : (
                                    <div className={Styles.todoContent}>
                                        <input
                                            type='checkbox'
                                            checked={entry.done}
                                            onChange={() => updateTodo(entry._id, { done: !entry.done })}
                                        />
                                        <div className={Styles.textContainer}>
                                            <strong>{entry.title}</strong>
                                            <p>{entry.description}</p>
                                        </div>
                                        <button
                                            onClick={() => handleEdit(entry._id, entry.title, entry.description)}
                                            className={Styles.editButton}
                                        >
                                            Edit
                                        </button>
                                        <button
                                            onClick={() => deleteTodo(entry._id)}
                                            className={Styles.deleteButton}
                                        >
                                            Delete
                                        </button>
                                    </div>
                                )}
                            </div>
                        ))
                    ) : (
                        <p className={Styles.noTodoMessage}>No tasks available. Please add a new task.</p>
                    )
                )}
            </div>
        </div>
    )
}
